import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import {browserHistory} from 'react-router';
import "./cartItem.css";

export default class CartItem extends Component {
    onNavigateBook = () => {
        browserHistory.push("/book");
    }
    render(){
        return(
            <div className="cart-item">
                <div className="item-info">
                    <img  className="item-info-img" src="https://bookdepot-lmwv1z0x2r9fdffrc7q.netdna-ssl.com/covers/large/isbn978014/9780143188520-l.jpg"></img>
                    <div className="item-book-info">
                        <h5 className="item-book-title"><Link onClick={this.onNavigateBook} to={{
                    pathname: "/book",
                    state: {
                        id:bookItem.id,
                        title:bookItem.title,
                        author:bookItem.author,
                        category:bookItem.category,
                        genre:bookItem.genre,
                        format:bookItem.format,
                        isbn:bookItem.format,
                        description:bookItem.description,
                        publisher:bookItem.publisher,
                        image:bookItem.image,
                        price:bookItem.price,
                        size:bookItem.size,
                        publihedDate:bookItem.publihedDate,
                        language:bookItem.language
                    }
                }}>AND THE MOUNTAINS ECHOED</Link></h5>
                        <p className="item-book-author">by Kh. Hoseiny</p>
                    </div>
                </div>
                <div className="item-details">
                    <div className="item-quantity">
                        <p>0</p>
                        <a className="remove-item" href="#">REMOVE</a>
                    </div>
                    <div className="item-price"><p>$6.02</p></div>
                    <div className="item-total"><p>$12.04</p></div>
                </div>  
            </div>
        );
    }
}
