import React,  {Component} from "react";
import Slider from "react-slick";
import "../carousel/slick.css"; 
import "../carousel/slick-theme.css";
import "./bookCard.css";

export default class BookCard extends Component {
    constructor(props){
        super(props);
    }

    render() {
        const settings = {
            infinite: true,
            speed: 1000,
            slidesToShow: 1,
            slidesToScroll: 1,
            autoplay:true,
            dots: true,
          };

        const {bookItem} = this.props; 
        return(
            <div className="card">
                  <Slider {...settings} >
                <div className="card-details">
                    <img className="book-img" src={bookItem.image}/>
                    <div className="book-title">{bookItem.title}</div>
                </div>    
            </Slider>
            </div>

        );
    }
}
