import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import {browserHistory} from 'react-router';
import "../newArrivals/arrivalsElement.css";

export default class SellersElement extends Component{
    constructor(props){
        super(props);
    }
    onNavigateBook = () => {
        browserHistory.push("/book");
    }
    render () {
        const {bookItem} = this.props;
        return(
            <div className="card">
            <div className="card-details">
                <img className="book-img" src={bookItem.image}/>
                <div className="book-title"><Link to ={{
                    pathname: "/book",
                    state: {
                        id:1,
                        title:"The Kite Runner",
                        author:"Khalid Hoseiny",
                        category:"fiction",
                        genre:"drama",
                        format:"Hardcover",
                        isbn:"9780525559566",
                        description:"Afghanistan, 1975: Twelve-year-old Amir is desperate to win the local kite-fighting tournament and his loyal friend Hassan promises to help him. But neither of the boys can foresee what will happen to Hassan that afternoon, an event that is to shatter their lives. After the Russians invade and the family is forced to flee to America, Amir realises that one day he must return to Afghanistan under Taliban rule to find the one thing that his new world cannot grant him: redemption.",
                        publisher:"The Kite Press",
                        image:"https://images.penguinrandomhouse.com/cover/9781101666227",
                        price:"6",
                        size:"18",
                        publihedDate:"2011-12-07",
                        language:"English"
                    }
                }} onClick={e => console.log(e.target)}>{bookItem.title}</Link></div>
                <div className="book-author">by {bookItem.author}</div>
            </div>    
        </div>

        );   
    }
}
