import React, { Component } from "react";
import Slider from "react-slick";
import Text from "./text/Text";
import "./slick.css"; 
import "./slick-theme.css";
import "./carousel.css";


export default class Carousel extends Component {
  render() {
    const settings = {
      infinite: true,
      speed: 1000,
      slidesToShow: 1,
      slidesToScroll: 1,
      autoplay:false,
      dots: true,
    };
    return (
      <div className="carousel-class">
        <Slider {...settings}>
          <div className="main-page pages">
                <h1 className="text-title"> Welcome to <span>Craft</span>!</h1>
                <p className="text-desc">Explore book recommendations, author events, gifts, and more from Craft's landmark bookstore.</p>
        </div>
          <div className="second-page pages">
           <Text className="text2" desc="BUY YOUR FIRST BOOK AND 
              GET 20% OFF YOUR NEXT PURCHASE"/>
          </div>
          <div>
            <h3>3</h3>
          </div>
        </Slider>
      </div>
    );
  }
}
