import React, { Component } from 'react';
import ResultBook from "./ResultBook";
import "./resultList.css";

export default class ResultList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            search: "Khalid"
        };
    }

    updateSearch(e) {
        this.setState({
            search: e.target.value.substr(0,20)
        });
    }

    render() {
        const db = this.props;
        let filteredBooks = db.resultList.filter(
            (book) => {
                return book.title.indexOf(this.state.search !== -1) 
                // book.author.indexOf(this.state.search !== -1) ||
                // book.description.indexOf(this.state.search !== -1)||
                // book.genre.indexOf(this.state.search !== -1) )||
                // book.category.indexOf(this.state.search !== -1) ||
                // book.format.indexOf(this.state.search !== -1)
            }
        );
        const resultElements = filteredBooks.map(book => {
            return <ResultBook key={book.id} resultItem={book} />
        })

            return (
                <div>
                    {resultElements}
                </div>
            );
        }
}
