import React, {Component} from 'react';
import {Link} from "react-router-dom";
import "./auth.css";

export default class SignUp extends Component{
constructor(){
    super();
    this.handleSubmit = this.handleSubmit.bind(this);
    this.state = {
        username: "",
        email: "",
        password:"",
        isAdmin: false,
        hasAgreed:false,
        usernameError:"",
        emailError: "",
        passwordError:"",
    }
}

    handleChange = (e) =>{
          let value = e.target.type === "checkbox" ? e.target.checked : e.target.value;
            this.setState({
                [e.target.name] : value,
            })
    }

    validate = () => {
        let usernameError = "";
        let emailError =  "";
        let passwordError = "";

        if (this.state.email.incudes('@')){
            emailError == "Invalid Email";
        }
    }

    handleSubmit(e){
        e.preventDefault();
   }

   render() {
        return (
            <div className="auth-page">
                <div className="auth-page-box">
                    <h2 className="auth-title">Sign Up</h2>
                    <div className="validate">
                        <h6>*{this.state.emailError}</h6>
                        <h6>*{this.state.usernameError}</h6>
                        <h6>*{this.state.passwordError}</h6>
                    </div>
                    <form  onSubmit={e => this.handleSubmit(e)} className="auth-form" method="post">
                        <label className="auth-label">Email: </label>
                        <input onChange={e => this.handleChange(e)} type="text" name="email" className="auth-input" value={this.state.email} required/>
                        <label className="auth-label">Password: </label>
                        <input type="password" onChange={e => this.handleChange(e)} name="password" className="auth-input" value={this.state.password}  required/>
                        <label for="terms">
                            <input type="checkbox" className="checkbox" name="terms" value={this.state.hasAgreed} onChange={e => this.handleChange(e)}/>
                                I agree with all statements in <a className="terms" href="#">terms of service</a>
                        </label>
                        <button type="submit" className="auth-button">Sign Up </button>
                    </form>
                    <h5 className="offer">Have an account? <Link to="login">Log In</Link></h5>
                </div>
            </div>
        );
    }
}
