import React, { Component } from 'react';
import ResultList from './ResultList';
import db from '../home/cards/db';
import Sidebar from "../home/sidebar/SideBar";
import "./searchResult.css";

export default class SearchResult extends Component{
    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
        this.handleOutsideClick = this.handleOutsideClick.bind(this);

        this.state = {
          popupVisible: false
        };

      }

      sortByPrice () {
        db.books.sort((a,b) => {
          if (a.price > b.price) {
            return 1;
          }
          if (a.price < b.price) {
            return -1;
          }
          // a должно быть равным b
          return 0;
        })
      }

      sortByYear () {
        db.books.sort((a,b) => {
          if (a.publishedDate > b.publishedDate) {
            return 1;
          }
          if (a.publishedDate < b.publishedDate) {
            return -1;
          }
          // a должно быть равным b
          return 0;
        })
      }

      handleClick() {
        if (!this.state.popupVisible) {
          // attach/remove event handler
          document.addEventListener('click', this.handleOutsideClick, false);
        } else {
          document.removeEventListener('click', this.handleOutsideClick, false);
        }

        this.setState(prevState => ({
           popupVisible: !prevState.popupVisible,
        }));
      }

      handleOutsideClick(e) {
        // ignore clicks on the component itself
        if (this.node.contains(e.target)) {
            return;
        }
        this.handleClick();
      }

    render(){
      const {search1} = this.props;
        return (
            <div className="search-results">
                <div className="result-items">
                    <div className="results-info">
                        <h1 className="search-result-title">Search Results</h1>
                        <h5 className="result-count">n item(s) found for "searchResponse"</h5>
                        <div className="dropdown" ref={node => { this.node = node; }}>
                    <button onClick={this.handleClick} className="dropbtn">Sort By<i className="arrow down"></i></button>
                    {this.state.popupVisible && (
                        <div id="myDropdown" className="dropdown-content">
                            <a href="#" onClick={this.sortByPrice}>Price</a>
                            <a href="#">Published Date</a>
                            <a href="#">Asc</a>
                            <a href="#">Desc</a>
                        </div>)}
                </div>
                    </div>
                        <ResultList resultList = {db.books} searchString={search1}/>
                </div>    
                <div className="">
                    <Sidebar/>
                </div>

                </div>
        );
    }
}
