import React, { Component } from 'react';
import ResultList from './ResultList';
import db from '../home/cards/db';
import Sidebar from "../home/sidebar/SideBar";
import "./searchResult.css";

export default class SearchResult extends Component{
    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
        this.handleOutsideClick = this.handleOutsideClick.bind(this);

        this.state = {
          popupVisible: false
        };
        db.books.push( {
          "id":"3",
          "title":"Thesfdf",
          "author":"Khasdsdny",
          "category":"sd",
          "genre":"dradsma",
          "format":"Hardcover",
          "isbn":"9780525559566",
          "description":"Afghanistan, 1975: Twelve-year-old Amir is desperate to win the local kite-fighting tournament and his loyal friend Hassan promises to help him. But neither of the boys can foresee what will happen to Hassan that afternoon, an event that is to shatter their lives. After the Russians invade and the family is forced to flee to America, Amir realises that one day he must return to Afghanistan under Taliban rule to find the one thing that his new world cannot grant him: redemption.",
          "publisher":"The Kite Press",
          "image":"https://images.penguinrandomhouse.com/cover/9781101666227",
          "price":"6",
          "size":"18",
          "publihedDate":
          "2011-12-07",
          "language":"English"
      },);
        console.log(db.books);
      }

      handleClick() {
        if (!this.state.popupVisible) {
          // attach/remove event handler
          document.addEventListener('click', this.handleOutsideClick, false);
        } else {
          document.removeEventListener('click', this.handleOutsideClick, false);
        }

        this.setState(prevState => ({
           popupVisible: !prevState.popupVisible,
        }));
      }

      handleOutsideClick(e) {
        // ignore clicks on the component itself
        if (this.node.contains(e.target)) {
            return;
        }
        this.handleClick();
      }

    render(){
      const {search1} = this.props;
        return (
            <div className="search-results">
                <div className="result-items">
                    <div className="results-info">
                        <h1 className="search-result-title">Search Results</h1>
                        <h5 className="result-count">n item(s) found for "searchResponse"</h5>
                        <div className="dropdown" ref={node => { this.node = node; }}>
                    <button onClick={this.handleClick} className="dropbtn">Sort By<i className="arrow down"></i></button>
                    {this.state.popupVisible && (
                        <div id="myDropdown" className="dropdown-content">
                            <a href="#">Price</a>
                            <a href="#">Year</a>
                            <a href="#">Asc</a>
                            <a href="#">Desc</a>
                        </div>)}
                </div>
                    </div>
                        <ResultList resultList = {db.books} searchString={search1}/>
                </div>    
                <div className="">
                    <Sidebar/>
                </div>

                </div>
        );
    }
}
