import React, { Component }  from 'react';
import  MenuItem from '@material-ui/core/MenuItem';
import MenuList from '@material-ui/core/MenuList';
import {Link} from "react-router-dom";
import "./sidebar.css";

export default class SideBar extends Component {
    constructor() {
        super();
        this.state = {
            categories: ["Fiction","Children","Comic","Health","Biographies","History","Science"],
            isOpen:true,
        }
    }

    handleCategoriesClick = () => {
        this.setState({
            isOpen:true,
        });
    }
    render() {
        return(
             <MenuList className="menu-list">
                <MenuItem className="menu-item" componrnt={Link} to="/">New Arrivals</MenuItem>
                <MenuItem className="menu-item" componrnt={Link} to="/">Bestsellers</MenuItem>
                <MenuItem className="menu-item" componrnt={Link} onClick="handleCategoriesClick()" to="/">Categories</MenuItem>
                {this.state.isOpen ? (
                     <MenuList >
                        {this.state.categories.map((id,name) => {
                            return <MenuItem componrnt={Link} to="/" className=" menu-item menu-item--nested" key={id}>{this.state.categories[name]}</MenuItem>
                        })}
                    </MenuList>):null
                }
            </MenuList>

        );
    }
}
