import React, {Component} from "react";
import BookCard from "./BookCard";
import Slider from "react-slick";
import "../carousel/slick.css"; 
import "../carousel/slick-theme.css";
import "./bookCardsList.css";

export default function BookCardsList(db) {
    const BookElements = db.bookElements.map(book => {
       return <BookCard key={book.books_id} bookItem={book} />
    })


    const settings = {
            dots: true,
            infinite: false,
            speed: 300,
            slidesToShow: 3,
            slidesToScroll: 1,   
            responsive: [
                {
                  breakpoint: 1024,
                  settings: {
                    slidesToShow: 3,
                    slidesToScroll: 3,
                    infinite: true,
                    dots: true
                  }
                },
                {
                  breakpoint: 600,
                  settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2
                  }
                },
                {
                  breakpoint: 480,
                  settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1
                  }
                }
                // You can unslick at a given breakpoint now by adding:
                // settings: "unslick"
                // instead of a settings object
              ]       
          };


    return (
        <div className="book-elements">
            <Slider {...settings} >
              {BookElements}
              </Slider>
        </div>
    );

}
