import React,  {Component} from "react";
import Slider from "react-slick";
import "../carousel/slick.css"; 
import "../carousel/slick-theme.css";
import "./bookCard.css";

export default class BookCard extends Component {
    constructor(props){
        super(props);
    }
    render() {
        const settings = {
            dots: false,
            infinite: false,
            speed: 300,
            slidesToShow: 3,
            slidesToScroll: 1,   
            responsive: [
                {
                  breakpoint: 1024,
                  settings: {
                    slidesToShow: 3,
                    slidesToScroll: 3,
                    infinite: true,
                    dots: true
                  }
                },
                {
                  breakpoint: 600,
                  settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2
                  }
                },
                {
                  breakpoint: 480,
                  settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1
                  }
                }
                // You can unslick at a given breakpoint now by adding:
                // settings: "unslick"
                // instead of a settings object
              ]       
          };

        const {bookItem} = this.props; 
        return(
            <div className="card">
                  <Slider {...settings} >
                <div className="card-details">
                    <img className="book-img" src={bookItem.image}/>
                    <div className="book-title">{bookItem.title}</div>
                </div>    
            </Slider>
            </div>

        );
    }
}
