import React, {Component} from 'react';
import {Link} from "react-router-dom";
import "./auth.css";

const emailRegex = RegExp(/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/);

const formValid = formErrors => {
    let valid = true;
    Object.values(formErrors).forEach(val => {val.length>0 && (valid = false)});
    return valid;
}

