        lastName:"",
        isAdmin: false,
        formErrors: {
            emailError: "",
            usernameError: "",
            passwordError: ""
        }
    }

    this.getPhp = this.getPhp.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
}

    handleChange(e) {
        e.preventDefault();
        let target = e.target;
        let value = target.value;
        let name = target.name;
        let formErrors = this.state.formErrors;

        console.log(value);
        if (name ==="isAdmin"){} else {
            switch(name) {
                case 'email':
                    formErrors.emailError = 
                    emailRegex.test(value) && value.length > 0 ? this.setState({[name]:value}) : "Invalid Email Address";
                    break;
                case 'username':
                        formErrors.usernameError = value.length < 3 && value.length>0 ? 'Username should contain at least 6 charachters': '';
                    break
                case 'password':
                    formErrors.passwordError = value.length < 6 && value.length>0 ? 'Password should contain at least 6 charachters': '';
            }
        }
    }   

    handleSubmit(e){
        e.preventDefault();
        if (formValid(this.state.formErrors)){
        }
        else {
            console.error("FORM INVAID DISPLAY");
        }
   }

   getPhp(){
    fetch('http://localhost/craft/api/demo.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
        })
      }).then(res => res.json()).then(response => 
       response.map(el => {
           console.log(el.email);
       }))

   }

   render() {
        return (
            <div className="auth-page">
                <div className="auth-page-box">
                    <h2 className="auth-title">Sign Up</h2>
                    <div className="validate">
                        <h6>{this.state.emailError}</h6>
                        <h6>{this.state.usernameError}</h6>
                        <h6>{this.state.passwordError}</h6>
                    </div>
                    <form onSubmit={e => this.handleSubmit}  className="auth-form" method="post">
                        <label className="auth-label">Email: </label>
                        <input onBlur={e => this.handleChange(e)} type="email" name="email" className="auth-input" value={this.state.email} />
                        <label className="auth-label">Username: </label>
                        <input onChange={e => this.handleChange(e)} type="text" name="username" className="auth-input" value={this.state.username} />
                        <label className="auth-label">Password: </label>
                        <input type="password" onChange={e => this.handleChange(e)} name="password" className="auth-input" value={this.state.password}  />
                        <label className="terms">By clicking sign up you agree to Craft's Terms amd Conditions and Privacy Policy.</label>
                        <button type="submit" className="auth-button">Sign Up </button>
                    </form>
                    <h5 className="offer">Have an account?  <Link className="to-link" to="login">Log In</Link></h5>
                </div>
                <div className="auth-page-rest">
                    <h3 className="page-rest-off">Join and Get 35% Off</h3>
                    <ul className="page-rest-list">
                        <li className="list-element">Recieve Exclusive tips, insparations, and offers</li>
                        <li className="list-element">Get 35% off your first book!</li>
                        <li className="list-element">Download free book-making tools and templates</li>
                        <li className="list-element">Stay up-to-date on new products, webinars, and events</li>
                    </ul>
                </div>
            </div>
        );
    }
}
