import React, { Component }  from 'react';
import  MenuItem from '@material-ui/core/MenuItem';
import MenuList from '@material-ui/core/MenuList';
import "./sidebar.css";
import { Router, Route} from 'react-router'
import { Link } from 'react-router-dom';
import NewArrivals from '../../newArrivals/NewArrivals';
import Category from '../../category/Category';

export default class SideBar extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isOpen:false,
        }
       }

    handleClick = (e) => {
        e.preventDefault();
        console.log('clicked');
        this.setState({
            isOpen: !this.state.isOpen,
        });
    }

    render() {
        return(
            <div>
               <MenuList className="menu-list">
                <MenuItem className="menu-item"><Link to="/arrivals">Arrivals</Link></MenuItem>
                <MenuItem className="menu-item"><Link to ="/bestsellers">Bestsellers</Link></MenuItem>
                <MenuItem className="menu-item" onClick={this.handleClick}>Categories</MenuItem>
                {this.state.isOpen ? (
                     <MenuList >
                             <MenuItem className=" menu-item menu-item--nested" ><Link to={`/categories/`}>Fiction</Link></MenuItem>
                             <MenuItem className=" menu-item menu-item--nested" ><Link to={`/categories/non-fiction`}>Non-Fiction</Link></MenuItem>
                             <MenuItem className=" menu-item menu-item--nested" ><Link to={`/categories/biographies`}>Biography</Link></MenuItem>
                             <MenuItem className=" menu-item menu-item--nested" ><Link to={`/categories/history`}>History</Link></MenuItem>

                    </MenuList>):null
                }
                                <MenuItem className="menu-item">About Us</MenuItem>
            </MenuList>
        </div>
        );
    }
}
