import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import "./resultBook.css";

export default class ResultBook extends Component {
    constructor(props){
        super(props);
    }
    render () {
        const {resultItem} = this.props;
        return (
            <div className="result-book">
                <img className="result-book-img" src={resultItem.image}></img>
                <div className="result-book-info">
                    <div className="result-book-name">
                        <a className="result-book-title"><Link to={{
                    pathname: "/book",
                    state: {
                        id:resultItem.id,
                        title:resultItem.title,
                        author:resultItem.author,
                        category:resultItem.category,
                        genre:resultItem.genre,
                        format:resultItem.format,
                        isbn:resultItem.format,
                        description:resultItem.description,
                        publisher:resultItem.publisher,
                        image:resultItem.image,
                        price:resultItem.price,
                        size:resultItem.size,
                        publihedDate:resultItem.publihedDate,
                        language:resultItem.language
                    }
                }}>{resultItem.title} </Link>| {resultItem.author}</a>
                    </div>
                    <h4 className="result-book-genre"><span>Genre: </span><a>{resultItem.genre}</a></h4>

                    <p className="result-book-overview">{resultItem.desciption}</p>
                    <a className="preview-link">preview</a>
                </div>
            </div>
        );
    }
}
