import React, {Component} from "react";
import { Route, Link } from "react-router-dom";
import Home from "../home/Home";
import "./header.css";

class Header extends Component {
    constructor() {
        super();
        this.handleClick = this.handleClick.bind(this);
        this.handleOutsideClick = this.handleOutsideClick.bind(this);

        this.state = {
          popupVisible: false
        };
      }

      handleClick() {
        if (!this.state.popupVisible) {
          // attach/remove event handler
          document.addEventListener('click', this.handleOutsideClick, false);
        } else {
          document.removeEventListener('click', this.handleOutsideClick, false);
        }

        this.setState(prevState => ({
           popupVisible: !prevState.popupVisible,
        }));
      }

      handleOutsideClick(e) {
        // ignore clicks on the component itself
        if (this.node.contains(e.target)) {
            return;
        }

        this.handleClick();
      }

    render() {
        return (
            <div>
                <div className="header">
                    <a className="logo" href="#">Craft</a>
                    <nav className="nav">
                        <Link className="nav__link" to="/home">Home</Link>
                        <Link className="nav__link" to="/cart">Cart</Link>
                        <div className="dropdown" ref={node => { this.node = node; }}>
                            <button onClick={this.handleClick} className="dropbtn"><Link className="nav__link">My Account</Link><i className="arrow down"></i></button>
                            {this.state.popupVisible && (
                            <div id="myDropdown" className="dropdown-content">
                            <Link>Log In</Link>
                            <a href="#">Sign Up</a>
                            <a href="#">Wishlist</a>
                        </div>)}
                </div>
                    </nav>
                    <div className="search-filed">
                        <input type="text" className="search-input" placeholder="Search books"></input>
                        <button className="search-button">Search</button>
                    </div>
            </div>
        <Route exact path="/home" component={Home}/>
        </div>
        );
    }
}

export default Header;
