import React, {Component} from 'react';
import "./auth.css";

export default class SignUp extends Component{
constructor(){
    super();
    this.state = {
        username: "",
        email: "",
        password:"",
        isAdmin: false,
    }
}

    onChange = (e) =>{
       /* for checkboxes
       const value = e.target.checked === undefined ? e.target.value : e.target.checked;
        this.setState({
            [e.target.name]:value,
        })*/
        if (e.target.name === "isAdmin") {}
        else {
            let value = e.target.value;    
            this.setState({
                [e.target.name] : value,
            })
        }


    }

   render() {
        return (
            <div className="auth-page">
                <div className="auth-page-box">
                    <h2 className="auth-title">Sign Up</h2>
                    <form className="auth-form" method="post">
                        <label className="auth-label">Email: </label>
                        <input onChange={e => this.onChange(e)} type="text" name="email" className="auth-input" value={this.state.email} required/>
                        <label className="auth-label">Password: </label>
                        <input type="password" name="password" className="auth-input" required/>
                        <label className="auth-label">Confirm Password: </label>
                        <input type="password" name="password" className="auth-input" required/>
                    </form>
                    <button type="submit" className="auth-button">Sign Up </button>
                </div>
            </div>
        );
    }
}
