import React, {Component} from 'react';
import "./auth.css";

export default class SignUp extends Component{
constructor(){
    super();
    this.state = {
        username: "",
        email: "",
        password:"",
        isAdmin: false,
        passConfirm:"",
        isPasswordConfirmed:"false",
    }
}

    onChange = (e) =>{
       /* for checkboxes
       const value = e.target.checked === undefined ? e.target.value : e.target.checked;
        this.setState({
            [e.target.name]:value,
        })*/
        let value = e.target.type === "checkbox" ? e.target.checked : e.target.value;
            this.setState({
                [e.target.name] : value,
            })


    }

   render() {
        return (
            <div className="auth-page">
                <div className="auth-page-box">
                    <h2 className="auth-title">Sign Up</h2>
                    <form className="auth-form" method="post">
                        <label className="auth-label">Email: </label>
                        <input onChange={e => this.onChange(e)} type="text" name="email" className="auth-input" value={this.state.email} required/>
                        <label className="auth-label">Password: </label>
                        <input type="password" onChange={e => this.onChange(e)} name="password" className="auth-input" value={this.state.password}  required/>
                        <input type="checkbox" className="checkbox" name="terms"/>
                        <label for="terms">I agree with all statements in <a className="terms" href="#">terms of service</a></label>
                    </form>
                    <button type="submit" className="auth-button">Sign Up </button>
                </div>
            </div>
        );
    }
}
