import React, {Component} from 'react';
import CategoryCard from "./CategoryCard";
import "./categoryList.css";

const genre = [{
    name:"Drama",
    img:""
},{
    name:"Romance",
    img:""
},
{
    name:"Adventure",
    img:""
},
{
    name:"Poetry",
    img:""
}];
const category = [{
    name:"Fiction",
    img: ""
},
{
    name:"Non-Fiction",
    img: ""
}];
export default class CategoryList extends Component {
    constructor(props){
        super(props);
        this.state={
            isGenre:false
        };
    }
    render() {
        if(this.props.name==="genre"){
            this.setState({
                isGenre:true
            });
           const genElements = genre.map(el => {
                return   <CategoryCard title={el.name} image={el.img}></CategoryCard>
            });}
            else{
            const catElements = category.map(el => {
                return   <CategoryCard title={el.namr} image={el.img}></CategoryCard>
            });}

        return (
            <div className="category-list">
               {this.state.isGenre ? {genElements}: {catElements}}
               </div>

        );
    }
}
